import React from "react";
import PropTypes from "prop-types";
import { FormCheckbox, FormInput } from "shards-react";

export default function CheckboxGroup({
  title,
  options = [],
  type,
  ...restProps
}) {
  return (
    <fieldset {...restProps}>
      {options.length &&
        options.map((item, i) => (
          <FormCheckbox key={`option-${i + 1}`}>
            {!type ? item : <input className="p-0 m-0" style={{border: "1px solid #e1e5eb"}}/>}
          </FormCheckbox>
        ))}
    </fieldset>
  );
}

CheckboxGroup.propTypes = {
  // options: PropTypes.ReactNodeArray,
  title: PropTypes.string,
};
