import React from "react";
import {
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  FormSelect,
} from "shards-react";

const CustomSelect = ({
  label,
  options = [],
  selected,
  setSelectedPatient,
}) => (
  <div>
    <InputGroup className="mb-3">
      <InputGroupAddon type="prepend">
        <InputGroupText>{label}</InputGroupText>
      </InputGroupAddon>
      <FormSelect required>
        <option value="" onClick={() => setSelectedPatient("")}>
          Choose
        </option>
        {option[0].id ? options.map((option) => (
          <option
            value={option.id}
            onClick={() => setSelectedPatient(option.id)}
          >
            {option.name}
          </option>
        )) : options.map()}
      </FormSelect>
    </InputGroup>

    {/* <InputGroup className="mb-3">
      <FormSelect>
        <option>Choose</option>
        <option>...</option>
      </FormSelect>
      <InputGroupAddon type="append">
        <InputGroupText>Options</InputGroupText>
      </InputGroupAddon>
    </InputGroup> */}
  </div>
);

export default CustomSelect;
