import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { Container, Row, Card, CardHeader, CardBody, Col, Button } from "reactstrap";
import { FormInput } from "shards-react";
import PageSpinner from "../../components/common/PageSpinner";
import PageTitle from "../../components/common/PageTitle";

export default function SuperAdminProfile() {
  const dispatch = useDispatch();
  const profile = JSON.parse(localStorage.getItem("loggedInUser"));
  // const useSelector
  const [edit, setEdit] = useState(false);
  const [control, setControl] = useState(profile);

  const handleChange = ({ target }) => {
    setControl({ ...control, [target.name]: target.value });
  };

  const submitChanges = () => {
    let endpoint = editCompanyEndpoint
    delete control.createdAt;
    delete control.updatedAt;
    delete control.id;
    dispatch(updateCompanyData(endpoint, control));
    setUpdated 
  }

  if (!control) {
    return <PageSpinner />;
  }

  return (
    <Container style={{ minHeight: "100vh" }}>
      <Row noGutters className="page-header py-4">
        <PageTitle
          sm="4"
          title="Profile"
          subtitle="Dashboard"
          className="text-sm-left"
        />
      </Row>
      <Card style={{ width: "100%", maxWidth: "506px" }} className="mx-auto">
        <CardHeader className="border-bottom" style={{fontSize: '1.2rem', fontWeight: 'bold'}}>Company Profile</CardHeader>
        <CardBody>
          <Col className="d-flex flex-row align-items-center">
              <h6 className="mb-0 mr-2">Name:</h6>
              <FormInput
                defaultValue={`${control.name}`}
                disabled={!edit}
                name="name"
                style={
                  !edit
                    ? {
                        background: "transparent",
                        border: "none",
                        fontSize: "1rem",
                      }
                    : { fontSize: "1rem" }
                }
                onChange={handleChange}
              />
          </Col>
          <Col className="d-flex flex-row align-items-center">
              <h6 className="mb-0 mr-2">Email:</h6>
              <FormInput
                defaultValue={`${control.email}`}
                disabled={!edit}
                name="name"
                type="email"
                style={
                  !edit
                    ? {
                        background: "transparent",
                        border: "none",
                        fontSize: "1rem",
                      }
                    : { fontSize: "1rem" }
                }
                onChange={handleChange}
              />
          </Col>
          <Col className="d-flex flex-row align-items-center">
              <h6 className="mb-0 mr-2">Phone:</h6>
              <FormInput
                defaultValue={`${control.phone}`}
                disabled={!edit}
                name="name"
                type="email"
                style={
                  !edit
                    ? {
                        background: "transparent",
                        border: "none",
                        fontSize: "1rem",
                      }
                    : { fontSize: "1rem" }
                }
                onChange={handleChange}
              />
          </Col>
          <Col className="d-flex flex-row align-items-center">
              <h6 className="mb-0 mr-2">Role:</h6>
              <span>SuperAdmin</span>
          </Col>

          <div className="d-flex flew-row">
            <Button
              className="ml-3"
              onClick={edit ? submitChanges : () => setEdit(true)}
            >{edit ? "Submit Changes" : "Update Profile"}</Button>
            {edit && (
              <Button 
                theme="danger"
                className="ml-3"
                onClick={() => {
                  restore();
                  setEdit(false);
                  }}
              >
                Cancel Changes
              </Button>
            )

            }
          </div>
        </CardBody>
      </Card>
    </Container>
  );
}
