import React, { useEffect, useState } from "react";
import { Card, CardHeader, Col, Form, FormGroup } from "shards-react";

import { Store } from "../../flux";

export default function FormBuilder() {
  const [formItems, setFormItems] = useState();

  useEffect(() => {
    setFormItems(Store.getFormItems());
  }, [Store]);

  return (
    <Col
      className="bg-white border-1 mr-3 p-0 border card"
      style={{ boxShadow: "none", height: "100vh" }}
    >
      <Card style={{ boxShadow: "none" }}>
        <CardHeader className="border-bottom">
          <h5 className="m-0">Progress Intake Form</h5>
        </CardHeader>

        <Form id="progress-intake-form">
          {formItems.length && formItems.map(() => (
            <FormGroup className="mb-3">
              <label></label>
            </FormGroup>
          ))}
        </Form>    
      </Card>
    </Col>
  );
}
