import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import {
  Container,
  Row,
  Col,
  Card,
  CardHeader,
  Button,
  FormGroup,
  Alert,
  FormInput,
} from "shards-react";
import {
  getDietitians,
  editPatientEndpoint,
} from "../../apiConstants/apiConstants";

import PageTitle from "../../components/common/PageTitle";
import avatar1 from "../../images/avatars/1.jpg";
import { getAllDietitians } from "../../actions/superadmin/dietitian";
import { editPatient } from "../../actions/superadmin/patients";
import { useSelector } from "react-redux";
import { useHistory } from "react-router";
import CustomSelect from "../../components/components-overview/CustomSelect";

const PatientDetails = () => {
  const patient = JSON.parse(localStorage.getItem("patient"));
  const companyCode = JSON.parse(localStorage.getItem("loggedInUser"))
    .companyCode;
  const dispatch = useDispatch();
  const allDietitians = useSelector((state) => state.allDietitians);
  const editedPatient = useSelector((state) => state.editPatient);
  const [selectedDietitian, setSelectedDietitian] = useState();
  const [popUp, setPopUp] = useState(false);
  const [alert, setAlert] = useState(false);
  const [updateSubscription, setUpdateSubscription] = useState(false);
  const history = useHistory();

  useEffect(() => {
    console.log(allDietitians);
  }, [allDietitians]);

  useEffect(() => {
    if (editedPatient.isSuccessful) {
      setAlert(true);
    }
    console.log(editedPatient);
  }, [editedPatient]);

  useEffect(() => {
    if (alert) {
      setTimeout(() => {
        setAlert(false);
        window.location.reload();
      }, 2000);
    }
  }, [alert]);

  console.log(patient);

  const handleSubmit = () => {
    const control = {
      ...patient,
      age: `${patient.age}`,
      weight: `${patient.weight}`,
      assignedDietitian: selectedDietitian,
    };
    delete control.createdAt;
    delete control.updatedAt;
    delete control.id;
    let endpoint = editPatientEndpoint + patient.id;
    console.log(endpoint);
    dispatch(editPatient(endpoint, control));
  };

  useEffect(() => {
    if (popUp) {
      let endpoint = getDietitians + patient.companyCode;
      dispatch(getAllDietitians(endpoint));
    }
  }, [popUp]);

  useEffect(() => {
    let endpoint = getDietitians + patient.companyCode;
    dispatch(getAllDietitians(endpoint));
  }, []);

  let assignedDietitianArr =
    allDietitians.data &&
    allDietitians.data.filter(
      (item) => item.Dietitian.id === patient.assignedDietitian
    );
  let assignedDietitian = assignedDietitianArr ? assignedDietitianArr[0] : null;
  console.log(assignedDietitian);

  const close = () => {
    setPopUp(false);
    setSelectedDietitian("");
  };

  const cancelEdit = () => {
    setUpdateSubscription(false);
  };

  console.log(selectedDietitian);

  return (
