import React, { useState, useEffect } from "react";
import {
  Container,
  Button,
  Form,
  FormGroup,
  FormInput,
  Col,
  Row,
} from "shards-react";
import * as ActionTypes from "../../actions/Types";
import PageTitle from "../../components/common/PageTitle";
import { useDispatch, useSelector } from "react-redux";
import { createDieticianEndpoint } from "../../apiConstants/apiConstants";
import { createDietician } from "../../actions/superadmin/dietician";

export default function CreateDietician() {
  const companyCode = useSelector((state) => state.authState.user.companyCode);
  const createDieticianState = useSelector((state) => state.createDietician);

  const createDieticianStateToDefault = () => {
    return {
      type: ActionTypes.CREATE_DIETICIAN_DEFAULT,
    }
  }

  useEffect(() => {
    if (createDieticianState.isCreated) {
      alert("Dietician Created");
      dispatch(createDieticianStateToDefault
      );
    }
  }, [createDieticianState]);

  const [control, setControl] = useState({
    role: "dietitian",
    isEmailVerified: true,
    companyCode,
  });
  const dispatch = useDispatch();

  const handleChange = ({ target }) => {
    setControl({
      ...control,
      [target.name]: target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    dispatch(createDietician(createDieticianEndpoint, control));
  };

  return (
    <Container fluid className="main-content-container px-4 pb-4">
      <Row noGutters className="page-header py-4">
        <PageTitle
          sm="4"
          title="Create Dietician"
          subtitle="Dashboard"
          className="text-sm-left"
        />
      </Row>
      <Col fluid className="d-flex flex-column align-items-center m-0">
        <Col
          className="bg-white card p-4"
          style={{
            width: "100%",
            maxWidth: "500px",
          }}
        >
          <h4 className="mb-4 text-center">Create Dietician</h4>
          <Form id="dietician_Login" onSubmit={handleSubmit}>
            <FormGroup>
              <label htmlFor="name">Name</label>
              <FormInput
                required
                onChange={handleChange}
                id="name"
                name="name"
                placeholder="Name"
                style={{ padding: ".5rem .75rem", fontSize: ".9rem" }}
              />
            </FormGroup>
            <FormGroup>
              <label htmlFor="emailAdress" style={{ fontSize: "1rem" }}>
                Email
              </label>
              <FormInput
                required
                onChange={handleChange}
                id="emailAddress"
                name="email"
                type="email"
                placeholder="Email"
                className="form-custom"
                style={{ padding: ".5rem .75rem", fontSize: ".9rem" }}
              />
            </FormGroup>
            <FormGroup>
              <label htmlFor="phone">Phone</label>
              <FormInput
                required
                onChange={handleChange}
                id="phone"
                name="phone"
                placeholder="Phone"
                style={{ padding: ".5rem .75rem", fontSize: ".9rem" }}
              />
            </FormGroup>
            <FormGroup>
              <label htmlFor="password">Password</label>
              <FormInput
                required
                onChange={handleChange}
                id="password"
                name="password"
                type="password"
                placeholder="Password"
                style={{ padding: ".5rem .75rem", fontSize: ".9rem" }}
              />
            </FormGroup>
            {/* <Col className="text-right form-group">
            <Link to="#">Forgot password?</Link>
          </Col> */}
            <Col className="p-0">
              <Button
                type="submit"
                className="w-100 bg-custom btn-custom"
                style={{ fontSize: "1rem" }}
                form="dietician_Login"
              >
                Create Dietician
              </Button>
            </Col>
          </Form>
        </Col>
      </Col>
    </Container>
  );
}
