// import axios from "axios";
import * as nprogress from "nprogress";

import { baseUrl } from "../apiConstants/apiConstants";
import * as ActionTypes from "./Types";
import onErrors from './errorHandler';

nprogress.configure({ showSpinner: false, easing: "ease", speed: 500 });

export const getData = (url, done) => {
  const endpoint = baseUrl + url;
  nprogress.start();
  return (dispatch) => {
    fetch(endpoint, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        nprogress.done();
        nprogress.remove();
        dispatch(done(data));

        dispatch(onErrors(ActionTypes.NO_ERRORS, false))
      })
      .catch((err) => {
        nprogress.done();
        nprogress.remove();
        dispatch(onErrors(ActionTypes.GET_ERRORS, err));
      });
  };
};

export const postData = (url, payload, done) => {
  const endpoint = baseUrl + url;
  nprogress.start();
  console.log(JSON.stringify(payload));
  let obj = {
    "name": "Cad Company",
    "email": "cadtestcompany@test.com",
    "phone": "123456789",
    "address": {
      "addressLine1": "Lot 6584",
      "addressLine2": "Pitfour",
      "parish": "St, James",
      "city": "Montego Bay",
      "zip": "JM035"
    },
    "userInfo": {
      "userName": "Cad Admin",
      "userEmail": "cmtest@test.com",
      "userPhone": "123456789"
    },
    "companyType": "Health"
  }
  return dispatch => {
    fetch(endpoint, {
      method: 'POST',
      body: obj,
      credentials: 'same-origin',
      headers: new Headers({
        'Content-Type': 'application/json',
        'Accept': 'application/json',
      }),
    })
    .then(res => res.json())
    .then(data => {
      nprogress.done()
      nprogress.remove();
      dispatch(done(data.data))

      dispatch(onErrors(ActionTypes.NO_ERRORS, false));
    })
    .catch(err => {
      nprogress.done();
      nprogress.remover();
      console.error(err);
      dispatch(onErrors(ActionTypes.GET_ERRORS, err))
    })
  }
}
