import React from "react";
import { Container, Card, CardHeader, Col, Row, FormGroup } from "shards-react";
import formItems from "../../data/form-items";

export default function FormCatalogue() {
  return (
    <Container fluid>
      <Row>
        <Col
          className="bg-white border-1 mr-3 p-0 border card"
          style={{ boxShadow: "none", maxWidth: "309" }}
        >
          <div className="p-0 border-bottom">
            <CardHeader className="p-3">
              <h4 className="m-0">Question Banks</h4>
            </CardHeader>
          </div>

          <Col
            style={{ maxHeight: "100vh", overflowY: "auto", overflowX: "none" }}
            className="p-0"
          >
            {formItems.map((item, i) => {
              return (
                <FormGroup
                  className={`${i !== formItems.length - 1 &&
                    "border-bottom"} p-3 m-0`}
                >
                  <label className="text-muted d-block mb-2">
                    {item.title}
                  </label>
                  <item.component
                    type={item.type}
                    options={item.options}
                    key={`item-${i + 1}`}
                  />
                </FormGroup>
              );
            })}
          </Col>
        </Col>

        <Col className="bg-white" style={{ height: "100vh" }}>
          <Card>

          </Card>
        </Col>
      </Row>
    </Container>
  );
}
