import React, { useEffect, useState } from "react";
import {
  Card,
  CardHeader,
  Col,
  Form,
  FormGroup,
  FormInput,
  FormTextarea,
} from "shards-react";

import { Store } from "../../flux";
import CheckboxGroup from "./CheckboxGroup";

export default function FormBuilder() {
  const [formItems, setFormItems] = useState(new Array("4"));

  useEffect(() => {
    setFormItems(Store.getFormItems());
  }, [Store]);

  return (
    <Col
      className="bg-white border-1 mr-3 p-0 border card"
      style={{ boxShadow: "none", height: "100vh" }}
    >
      <Card style={{ boxShadow: "none" }}>
        <CardHeader className="border-bottom">
          <h5 className="m-0">Progress Intake Form</h5>
        </CardHeader>

        <Form id="progress-intake-form">
          {formItems.map(({ item, i }) => (
            <FormGroup className="mb-3">
              {/* label */}
              <FormInput />
              {() => {
                let Component;
                switch (item.type) {
                  case "text":
                    Component = <FormInput />;
                  case "textarea":
                    return <FormTextarea />;
                  case "checkbox":
                    return <CheckboxGroup />;
                  case "radio":
                    return <CheckboxGroup />;
                  default:
                }
              }}
            </FormGroup>
          ))}
        </Form>
      </Card>
    </Col>
  );
}
