import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import {
  Container,
  Row,
  Col,
  Card,
  CardHeader,
  Button,
  FormGroup,
} from "shards-react";
import {
  getDietitians,
  editPatientEndpoint,
} from "../../apiConstants/apiConstants";

import PageTitle from "../../components/common/PageTitle";
import avatar1 from "../../images/avatars/1.jpg";
import { getAllDietitians } from "../../actions/superadmin/dietician";
import { editPatient } from "../../actions/superadmin/patients";
import { useSelector } from "react-redux";

const PatientDetails = () => {
  const patient = JSON.parse(localStorage.getItem("patient"));
  const dispatch = useDispatch();
  const allDieticians = useSelector((state) => state.allDietitians);
  const editedPatient = useSelector((state) => state.editPatient);
  const [selectedDietitian, setSelectedDietitian] = useState();
  const [popUp, setPopUp] = useState(false);

  useEffect(() => {
    console.log(allDieticians);
  }, [allDieticians]);

  useEffect(() => {
    if (editedPatient.isSuccessful) {
      setAlert(true);
    }
    console.log(editedPatient);
  }, [editedPatient]);

  console.log(patient);

  const handleSubmit = () => {
    const control = { ...patient, age: `${patient.age}`, weight: `${patient.weight}`, assignedDietitian: selectedDietitian };
    delete control.createdAt;
    delete control.updatedAt;
    delete control.id;
    let endpoint = editPatientEndpoint + patient.id;
    console.log(endpoint);
    dispatch(editPatient(endpoint, control));
  };

  useEffect(() => {
    if (popUp) {
      let endpoint = getDietitians + patient.companyCode;
      dispatch(getAllDietitians(endpoint));
    }
  }, [popUp]);

  useEffect(() => {
    dispatch(getAllDietitians())
  }, []);

  const close = () => {
    setPopUp(false);
    selectedDietitian("");
  };

  return (
    <Container fluid className="main-content-container px-4">
      <Row noGutters className="page-header py-4">
        <PageTitle
          title="Patient Profile"
          subtitle="Overview"
          md="12"
          className="ml-sm-auto mr-sm-auto"
        />
      </Row>
      <div
        className={`${popUp ? "d-block" : "d-none"}`}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: "100%",
          width: "100%",
          zIndex: 10,
          background: "rgba(0,0,0,.24",
        }}
      ></div>
      <Col
        lg="12"
        className="d-flex flex-column justify-content-center align-items-center mb-5"
      >
        <Card
          className={`${popUp ? "d-block" : "d-none"} pb-3`}
          style={{ position: "absolute", zIndex: 1000, width: 400, top: "30%" }}
        >
          <span
            class="material-icons"
            style={{
              position: "absolute",
              top: 10,
              right: 10,
              cursor: "pointer",
            }}
            onClick={() => close()}
          >
            close
          </span>
          <CardHeader className="border-bottom">
            Select Admin to assign to
          </CardHeader>
          <Col className={`p-3 pb-0 ${selectedDietitian && "border-bottom"}`}>
            <fieldset>
              {allDieticians.data &&
                allDieticians.data.map((dietitian, i) => {
                  return (
                    <FormGroup
                      key={`item-${i + 1}`}
                      style={{ cursor: "pointer" }}
                      className="mb-0"
                    >
                      <input
                        type="radio"
                        value={dietitian.id}
                        onChange={({ target }) =>
                          setSelectedDietitian(target.value)
                        }
                        className="mr-2"
                      />
                      <span>{dietitian.name}</span>
                    </FormGroup>
                  );
                })}
            </fieldset>
          </Col>
          {selectedDietitian && (
            <Col className="pt-2">
              <Button
                disabled={selectedDietitian ? false : true}
                className="ml-3"
                onClick={handleSubmit}
              >
                Assign
              </Button>
            </Col>
          )}
        </Card>
        <Card style={{ width: "100%", maxWidth: "500px" }}>
          <p
            style={{
              position: "absolute",
              top: "10px",
              right: "15px",
              color: "#52b5e9",
              border: "1px solid #52b5e9",
              borderRadius: "50px",
            }}
            className="py-1 px-2"
          >
            {patient.assignedDietitian !== undefined
              ? "Assigned"
              : "Unassigned"}
          </p>
          <CardHeader className="border-bottom text-center">
            <div className="mb-3 mx-auto">
              <img
                className="rounded-circle"
                src={avatar1}
                alt={patient.name}
                width="110"
              />
            </div>
            <h4 className="mb-0">{patient.name}</h4>
            <span className="text-muted d-block mb-2">{patient.email}</span>
            <span className="text-muted d-block mb-2">{patient.phone}</span>
            {patient.assignedDietitian === undefined && (
              <Button
                pill
                outline
                size="sm"
                className="mb-2"
                onClick={() => setPopUp(true)}
              >
                <i className="material-icons mr-1">person_add</i> Assign to
                Dietitian
              </Button>
            )}
          </CardHeader>
          <Col className="border-bottom">
            <h5 className="mb-1 mt-1">Bio Data</h5>
            <Col className="d-flex flex-row">
              <h6 className="mr-2">Age:</h6> {"  "}
              <span>{patient.age}</span>
            </Col>
            <Col className="d-flex flex-row">
              <h6 className="mr-2">Weight:</h6>
              {"  "}
              <span>{patient.weight}</span>
            </Col>
          </Col>
          <Col className="border-bottom">
            <h5 className="mb-1 mt-1">Health</h5>
            <Col className="d-flex flex-row">
              <h6 className="mr-2">Condition:</h6> {"  "}
              <span>{patient.healthCondition.join(", ").toString()}</span>
            </Col>
            <Col className="d-flex flex-row">
              <h6 className="mr-2">Goal:</h6>
              {"  "}
              <span>{patient.healthGoal}</span>
            </Col>
            <Col className="d-flex flex-row">
              <h6 className="mr-2">Food Prefence:</h6>
              {"  "}
              <span>{patient.foodPreference.join(", ").toString()}</span>
            </Col>
          </Col>
          {patient.assignedDietician && 
            <Col className="d-flex flex-row mb-3 mt-2">
            <h6 className="mr-2">Dietician:</h6> {"  "}
            <span>{patient.assignedDietician}</span>
          </Col>
          }
          <Col className="d-flex flex-row mb-3 mt-2">
            <h6 className="mr-2">Subscription:</h6> {"  "}
            <span>{patient.subscriptionPackage}</span>
          </Col>
        </Card>
      </Col>
    </Container>
  );
};

export default PatientDetails;
