import * as ActionTypes from "./Types";
import { postData } from "./services";

const onLogin = (payload) => {
  if(payload.data) {
    return {
      type: ActionTypes.LOGIN_SUCCESS,
    payload,
    }
  }
  return {
    type: ActionTypes.LOGIN,
    payload,
  };
};

export const login = (url, payload) => {
  return postData(url, payload, onLogin);
};
