import React, { useEffect, useState } from "react";
import {
  Card,
  CardHeader,
  Col,
  Form,
  FormGroup,
  FormInput,
  FormTextarea,
} from "shards-react";

import { Store } from "../../flux";
import CheckboxGroup from "./CheckboxGroup";

export default function FormBuilder() {
  const [formItems, setFormItems] = useState([]);

  useEffect(() => {
    setFormItems(Store.getFormItems());
  }, [Store]);

  const selectInputType = (item) => {
    console.log(item);
    let Component;
    switch (item.type) {
      case "text":
        Component = FormInput;
        console.log(item.type);
        break;
      case "textarea":
        Component = FormTextarea;
        console.log(item.type);
        break;
      case "checkbox":
        Component = CheckboxGroup;
        console.log(item.type);
        break;
      case "radio":
        Component = CheckboxGroup;
        console.log(item.type);
        break;
      default:
    }
    return <Component />;
  }

  return (
    <Col
      className="bg-white border-1 mr-3 p-0 border card"
      style={{ boxShadow: "none", height: "100vh" }}
    >
      <Card style={{ boxShadow: "none" }}>
        <CardHeader className="border-bottom">
          <h5 className="m-0">Progress Intake Form</h5>
        </CardHeader>

        <Form id="progress-intake-form">
          {formItems.length && formItems.map((item, i ) => (
            <FormGroup className="p-3 mb-0" key={`item-${i+1}`}>
              {/* label */}
              <FormInput
                style={{
                  borderLeft: 0,
                  borderRight: 0,
                  borderTop: 0,
                  borderRadius: 0,
                  fontSize: ".9rem",
                  fontWeight: 400,
                }}
                placeholder="Field Title"
                className="d-block border-bottom label-input"
              />
              {selectInputType(item)}
            </FormGroup>
          ))}
        </Form>
      </Card>
    </Col>
  );
}
