import axios from "axios";
import * as nprogress from "nprogress";

import ErrorHandler from "../reducers/errorReducer";
import { baseUrl } from "../apiConstants/apiConstants";
import { GET_ERRORS, NO_ERRORS } from "./Types";
import { errors } from "../reducers/initialState";

nprogress.configure({ showSpinner: false, easing: "ease", speed: 500 });

export const getData = (url, done) => {
  const endpoint = baseUrl + url;
  nprogress.start();
  return (dispatch) => {
    fetch(endpoint, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        nprogress.done();
        nprogress.remove();
        dispatch(done(data));
      })
      .catch((err) => {
        nprogress.done();
        nprogress.remove();

        let action = { type: GET_ERRORS, payload: err };
        dispatch(ErrorHandler(errors, action));
      });
  };
};
