import axios from "axios";
import * as nprogress from "nprogress";

import ErrorHandler from "../reducers/errorReducer";
import { baseUrl } from "../apiConstants/apiConstants";
import { GET_ERRORS, NO_ERRORS } from "./Types";
im

nprogress.configure({ showSpinner: true, easing: "ease", speed: 500 });

export const getData = (url, done) => {
  const endpoint = baseUrl + url;
  nprogress.start();
  return async (dispatch) => {
    try {
      const response = await axios.get(endpoint);

      const result = response.json();
      if (result) {
        nprogress.done();
        nprogress.remove();

        dispatch(done(result.data));
        dispatch(ErrorHandler({ type: NO_ERRORS, payload: false }));
      }
    } catch (err) {
      dispatch(ErrorHandler({ type: GET_ERRORS, payload: err }));
    }
  };
};
