import React from "react";
import { FormCheckbox, FormGroup } from "shards-react";

export default function CheckboxGroup({ title, options, ...restProps }) {
  return (
    <FormGroup {...restProps}>
      <strong className="text-muted d-block mb-2">{title}</strong>
      <fieldset>
        {options.length &&
          options.map((item, i) => (
            <FormCheckbox key={`option-${i + 1}`}>{item}</FormCheckbox>
          ))}
      </fieldset>
    </FormGroup>
  );
}

CheckboxGroup.propTypes = {
  options: Prop
}
