import React from  "react";
import { FormGroup } from "shards-react";

export default function RadioInputGroup({ title, options, ...restProps }) {
  <FormGroup {...restProps}>
    <label className="text-muted d-block mb-2">{title}</label>
    <fieldset>
      {options.length && options.map(({ item, i}) => (
        <FormRadio key={`option-${i+1}`}>{item}</FormRadio>
      ))}
    </fieldset>
  </FormGroup>
}
