import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Container, Row, Card, CardHeader, CardBody, Col } from "reactstrap";
import { FormInput } from "shards-react";
import PageSpinner from "../../components/common/PageSpinner";
import PageTitle from "../../components/common/PageTitle";

export default function SuperAdminProfile() {
  const dispatch = useDispatch();
  const profile = JSON.parse(localStorage.getItem("loggedInUser"));
  const [edit, setEdit] = useState(false);
  const [control, setControl] = useState({});

  useEffect(() => {
    console.log(profile);
    if (profile) {
      setControl({ ...profile });
    }
  }, [profile]);

  const handleChange = ({ target }) => {
    setControl({ ...control, [target.name]: target.value });
  };

  if (!profile) {
    return <PageSpinner />;
  }

  return (
    <Container style={{ minHeight: "100vh" }}>
      <Row noGutters className="page-header py-4">
        <PageTitle
          sm="4"
          title="Profile"
          subtitle="Dashboard"
          className="text-sm-left"
        />
      </Row>
      <Card style={{ width: "100%", maxWidth: "506px" }}>
        <CardHeader className="border-bottom">Company Profile</CardHeader>
        <CardBody>
          <Col className="d-flex flex-row align-items-center">
              <h6>Name</h6>
              <FormInput
                defaultValue={`${control.name}`}
                disabled={!edit}
                name="name"
                style={
                  !edit
                    ? {
                        background: "transparent",
                        border: "none",
                        fontSize: "1rem",
                      }
                    : { fontSize: "1rem" }
                }
                onChange={handleChange}
              />
          </Col>
          <Col className="d-flex flex-row align-items-center">
              <h6>Name</h6>
              <FormInput
                defaultValue={`${patient.name}`}
                disabled={!edit}
                name="name"
                style={
                  !edit
                    ? {
                        background: "transparent",
                        border: "none",
                        fontSize: "1rem",
                      }
                    : { fontSize: "1rem" }
                }
                onChange={handleChange}
              />
          </Col>
        </CardBody>
      </Card>
    </Container>
  );
}
