import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from 'react-redux';
import { Col, Row, Button } from "shards-react";
import { Link } from "react-router-dom";

import { allCompanies } from "../apiConstants/apiConstants";

export default function Landing() {
  const dispatch = useDispatch(); 
  const AllCompanies = useSelector(state => state.allCompanies);
  const [companies, setCompanies] = useState([]);

  useEffect(() => {
    dispatch(getAllCompanies(allCompanies))
  }, []);

  return (
    <Col className="d-flex justify-content-center pt-5">
      <Row
        noGutters
        className="page-header py-5 px-4 bg-white card landing-cont"
      >
        <h2 className="mb-3" style={{fontWeight: "600"}}>Welcome!</h2>
        <h4 className="mb-4 text-left">Select Company:</h4>
        <Row className="d-flex justify-content-center align-items-center">
          <Link to="#">
          <div
            className="btn-custom bg-custom my-2 btn card d-flex justify-content-center align-items-center btn btn-outline-primary"
            style={{
              textAlign: "center",
              fontWeight: "500",
              fontSize: "1.2rem",
              width: "200px",
              height: "200px",
              border: "1px solid #52b5e9",
            }}
          >
            Company 1
          </div>
          </Link>
        </Row>
      </Row>
    </Col>
  );
}
