export const getData = (url, done) => {
  const endpoint = baseUrl + url;
  nprogress.start();
  return (dispatch) => {
    fetch(endpoint, {
      method: "GET",
    })
      .then((res) => res.json())
      .then((data) => {
        nprogress.done();
        nprogress.remove();
        dispatch(done(data));

        let action = { type: ActionTypes.NO_ERRORS, payload: false };
        dispatch(ErrorHandler(errors, action));
      })
      .catch((err) => {
        nprogress.done();
        nprogress.remove();

        let action = { type: ActionTypes.GET_ERRORS, payload: err };
