import React from "react";
import { useSelector } from "react-redux";
import { useHistory } from "react-router";
import PageTitle from "../../components/common/PageTitle";
import { Container, Row, Col, Card, CardHeader, Button } from "shards-react";
import avatar1 from "../../images/avatars/1.jpg";

export default function DietitianPatients() {
  const patients = JSON.parse(localStorage.getItem("dietitianPatients"));
  console.log(patients);
  const history = useHistory();
  return (
    <Container fluid className="main-content-container px-4 pb-4">
      <Row noGutters className="page-header py-4">
        <PageTitle
          sm="4"
          title="Patients"
          subtitle="Dietitian"
          className="text-sm-left"
        />
      </Row>
      <Row
        style={{
          display: "grid",
          gridGap: "20px",
          gridTemplateColumns: "repeat(auto-fill,minmax(250px,1fr))",
          minHeight: "65vh",
        }}
        className="m-0"
      >
        {patients.length
          ? patients.map((patient, i) => {
              return (
                <Col className="p-0" key={`patient-${i + 1}`}>
                  <Card small className="p-3">
                    <CardHeader className="border-bottom text-center">
                      <div className="mb-3 mx-auto">
                        <img
                          className="rounded-circle"
                          src={avatar1}
                          alt="John Doe"
                          width="110px"
                        />
                      </div>
                      <h4 className="mb-0" style={{ fontSize: "1.3rem" }}>
                        {patient.name}
                      </h4>
                      <span
                        className="text-muted d-block mb-1"
                        style={{ fontSize: ".9rem" }}
                      >
                        {patient.email}
                      </span>
                      <span
                        className="text-muted d-block mb-2"
                        style={{ fontSize: ".9rem" }}
                      >
                        {patient.phone}
                      </span>
                    </CardHeader>
                    <div>
                      <div>
                        <strong
                          className="text-muted d-block mb-1 mt-2"
                          style={{ fontSize: ".955rem" }}
                        >
                          Health Conditions
                        </strong>
                        <p
                          style={{
                            fontSize: ".85rem",
                            lineHeight: "1.2rem",
                            width: "100%",
                            height: "38px",
                            // whiteSpace: "nowrap",
                            overflow: "hidden",
                            textOverflow: "ellipsis",
                            lineClamp: "2",
                          }}
                          className="mb-2"
                        >
                          {patient.healthCondition &&
                            patient.healthCondition.join(", ").toString()}
                        </p>
                      </div>
                    </div>
                    <Button
                      pill
                      outline
                      size="sm"
                      className="mb-2"
                      style={{ fontSize: ".85rem" }}
                      onClick={() => {
                        localStorage.setItem("patient",JSON.stringify(patient));
                        history.push(
                          "/superadmin/dietitian/patients/patient_details"
                        );
                      }}
                    >
                      View
                    </Button>
                  </Card>
                </Col>
              );
            })
          : ""}
      </Row>
    </Container>
  );
}
