import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Container, Row, Col, Card, CardHeader, Button } from "shards-react";
import { getAllDietitians } from "../../actions/superadmin/dietitian";
import { getDietitians } from "../../apiConstants/apiConstants";
import PageSpinner from '../../components/common/PageSpinner';

export default function DietitianDetails() {
  const dietitianId = JSON.parse(localStorage.getItem('dietitian')).id;
  const [dietitian, setDietitian] = useState();
  const dispatch = useDispatch();
  const allDietitians = useSelector((state) => state.allDietitians);

  useEffect(() => {
    let endpoint = getDietitians + companyCode;
    dispatch(getAllDietitians(endpoint));
  }, []);

  useEffect(() => {
    if(allDietitians.data) {
     let dietitianArr = allDietitians.data.filter(item => item.Dietitian.id === dietitianId);
     setDietitian(dietitianArr[0].Dietitian);
    }
  }, [allDietitians]);

  if(!dietitian) {
    return <PageSpinner />
  }

  return <Container>

  </Container>
}
