import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import {
  Container,
  Row,
  Col,
  Card,
  CardHeader,
  Button,
  FormGroup,
  Alert,
  FormInput,
} from "shards-react";
import {
  getDietitians,
  editPatientEndpoint,
} from "../../apiConstants/apiConstants";

import PageTitle from "../../components/common/PageTitle";
import avatar1 from "../../images/avatars/1.jpg";
import { getAllDietitians } from "../../actions/superadmin/dietitian";
import { editPatient } from "../../actions/superadmin/patients";
import { useSelector } from "react-redux";
import { useHistory } from "react-router";
import CustomSelect from "../../components/components-overview/CustomSelect";

const PatientDetails = () => {
  const patient = JSON.parse(localStorage.getItem("patient"));
  const companyCode = JSON.parse(localStorage.getItem("loggedInUser"))
    .companyCode;
  const dispatch = useDispatch();
  const allDietitians = useSelector((state) => state.allDietitians);
  const editedPatient = useSelector((state) => state.editPatient);
  const [selectedDietitian, setSelectedDietitian] = useState();
  const [popUp, setPopUp] = useState(false);
  const [alert, setAlert] = useState(false);

  const [edit, setEdit] = useState(false);
  const [foodPreference, setFoodPreference] = useState([
    ...patient.foodPreference,
  ]);
  const [healthCondition, setHealthCondition] = useState([
    ...patient.healthCondition,
  ]);
  const [currentFoodPreference, setCurrentFoodPreference] = useState("");
  const [currentHealthCondition, setCurrentHealthCondition] = useState("");

  const history = useHistory();

  useEffect(() => {
    if (editedPatient.isSuccessful) {
      setAlert(true);
    }
    console.log(editedPatient);
  }, [editedPatient]);

  useEffect(() => {
    if (alert) {
      setTimeout(() => {
        setAlert(false);
        window.location.reload();
      }, 2000);
    }
  }, [alert]);

  const handleSubmit = () => {
    const control = {
      ...patient,
      age: `${patient.age}`,
      weight: `${patient.weight}`,
      assignedDietitian: selectedDietitian,
    };
    delete control.createdAt;
    delete control.updatedAt;
    delete control.id;
    let endpoint = editPatientEndpoint + patient.id;
    console.log(endpoint);
    dispatch(editPatient(endpoint, control));
  };

  useEffect(() => {
    if (popUp) {
      let endpoint = getDietitians + patient.companyCode;
      dispatch(getAllDietitians(endpoint));
    }
  }, [popUp]);

  useEffect(() => {
    if (popUp) {
      let endpoint = getDietitians + patient.companyCode;
      dispatch(getAllDietitians(endpoint));
    }
  }, [popUp]);

  let assignedDietitianArr =
    allDietitians.data &&
    allDietitians.data.filter(
      (item) => item.Dietitian.id === patient.assignedDietitian
    );
  let assignedDietitian = assignedDietitianArr ? assignedDietitianArr[0] : null;
  // console.log(assignedDietitian);

  const close = () => {
    setPopUp(false);
    setSelectedDietitian("");
  };

  const addFoodPreference = () => {
    setFoodPreference([...foodPreference, currentFoodPreference]);
  };

  const addHealthCondition = () => {
    setHealthCondition([...healthCondition, currentHealthCondition]);
  };

  const [control, setControl] = useState(patient);

  const handleChange = ({ target }) => {
    setControl({ ...control, [target.name]: target.value });
  };

  const restore = () => {
    // setControl(patient);
  };

  const submitChanges = () => {
    let endpoint = editPatientEndpoint + patient.id;
    console.log(endpoint);
    // dispatch(editPatient(endpoint, control));
  };

  // console.log(selectedDietitian);

  if (control === {}) {
    return <div></div>;
  }

  return (
    <Container fluid className="main-content-container px-4">
      {alert && (
        <Container fluid className="px-0 sticky-top">
          <Alert className="mb-0 text-center">
            <i className="fa fa-info mx-2"></i>Assigned Patient to Dietitian!
          </Alert>
        </Container>
      )}
      <Row noGutters className="page-header py-4">
        <PageTitle
          title="Patient Profile"
          subtitle="Overview"
          md="12"
          className="ml-sm-auto mr-sm-auto"
        />
      </Row>
      <div
        className={`${popUp ? "d-block" : "d-none"}`}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          height: "100%",
          width: "100%",
          zIndex: 10,
          background: "rgba(0,0,0,.24",
        }}
      ></div>
      <Col
        lg="12"
        className="d-flex flex-column justify-content-center align-items-center mb-5"
      >
        <Card
          className={`${popUp ? "d-block" : "d-none"} pb-3`}
          style={{ position: "absolute", zIndex: 1000, width: 400, top: "30%" }}
        >
          <span
            class="material-icons"
            style={{
              position: "absolute",
              top: 10,
              right: 10,
              cursor: "pointer",
            }}
            onClick={() => close()}
          >
            close
          </span>
          <CardHeader className="border-bottom">
            Select Admin to assign to
          </CardHeader>
          <Col className={`p-3 pb-0 ${selectedDietitian && "border-bottom"}`}>
            <fieldset>
              {allDietitians.data &&
                allDietitians.data
                  .filter((item) => item.Dietitian.companyCode === companyCode)
                  .map((dietitian, i) => {
                    return (
                      <FormGroup
                        key={`item-${i + 1}`}
                        style={{ cursor: "pointer" }}
                        className="mb-1"
                      >
                        <input
                          type="radio"
                          value={dietitian.Dietitian.id}
                          onChange={({ target }) =>
                            setSelectedDietitian(target.value)
                          }
                          className="mr-2"
                        />
                        <span>{dietitian.Dietitian.name}</span>
                      </FormGroup>
                    );
                  })}
            </fieldset>
          </Col>
          {selectedDietitian && (
            <Col className="pt-2">
              <Button
                disabled={selectedDietitian ? false : true}
                className="ml-3"
                onClick={handleSubmit}
              >
                Assign
              </Button>
            </Col>
          )}
        </Card>
        <Card style={{ width: "100%", maxWidth: "500px" }} className="pb-3">
          <p
            style={{
              position: "absolute",
              top: "10px",
              right: "15px",
              color: "#52b5e9",
              border: "1px solid #52b5e9",
              borderRadius: "50px",
            }}
            className="py-1 px-2"
          >
            {control.assignedDietitian !== undefined
              ? "Assigned"
              : "Unassigned"}
          </p>
          <CardHeader className="border-bottom text-center">
            <div className="mb-3 mx-auto">
              <img
                className="rounded-circle"
                src={avatar1}
                alt={control.name}
                width="110"
              />
            </div>
            {!edit ? (
              <h4 className="mb-0">{control.name}</h4>
            ) : (
              <FormInput
                type="email"
                defaultValue={control.name}
                name="name"
                onChange={handleChange}
              />
            )}
            {!edit ? (
              <span className="text-muted d-block mb-2">{control.email}</span>
            ) : (
              <FormInput
                type="email"
                defaultValue={control.email}
                name="email"
                onChange={handleChange}
              />
            )}
            <span className="text-muted d-block mb-2">{control.phone}</span>
            {!edit ? (
              <span className="text-muted d-block mb-2">{control.phone}</span>
            ) : (
              <FormInput
                type="phone"
                defaultValue={control.phone}
                name="phone"
                onChange={handleChange}
              />
            )}
            {
              <Button
                pill
                outline
                size="sm"
                className="mb-2"
                onClick={() => setPopUp(true)}
              >
                <i className="material-icons mr-1">person_add</i> Assign to
                Dietitian
              </Button>
            }
          </CardHeader>
          <Col className={`border-bottom ${edit &&'pb-3'}`}>
            <h5 className="mb-1 mt-1">Bio Data</h5>
            <Col className={`d-flex flex-row align-items-center ${edit && 'mb-2'}`}>
              <h6 className={`mr-2 mb-0`}>Age:</h6>
              <FormInput
                defaultValue={`${control.age}`}
                disabled={!edit}
                style={!edit ? {background: "transparent", border: "none", fontSize: '1rem', color: "#5a6169"} : {fontSize: '1rem',  color: "#5a6169"}}
                name="age"
                onChange={handleChange}
              />
            </Col>
            <Col className="d-flex flex-row align-items-center">
              <h6 className="mr-2 mb-0">Weight:</h6>
              <FormInput
                defaultValue={`${control.weight}`}
                disabled={!edit}
                name="weight"
                style={!edit ? {background: "transparent", border: "none", fontSize: '1rem'} : {fontSize: '1rem'}}
                onChange={handleChange}
              />
            </Col>
          </Col>
          <Col className={`border-bottom ${edit &&'pb-3'}`}>
            <h5 className="mb-1 mt-1">Health</h5>
            <Col className={`d-flex ${edit ? 'flex-column mb-3' : 'flex-row'}`}>
              <h6 className="mr-2 mb-0">Condition:</h6>
              {edit && (
                <div className="d-flex flex-row">
                  <FormInput
                    name="foodPreference"
                    placeholder="Add food item"
                    style={{ width: 200 }}
                    className="ml-3 mr-3"
                    onChange={({ target }) =>
                      setCurrentHealthCondition(target.value)
                    }
                  />
                  <Button onClick={addHealthCondition}>
                    <span>+</span>Add
                  </Button>
                </div>
              )}
              <span className={`${edit && 'border-bottom'}`}>{healthCondition.join(", ").toString()}</span>
            </Col>
            <Col className={`d-flex flex-row align-items-center ${edit ? 'mb-3' : ''}`}>
              <h6 className="mr-2 mb-0">Goal:</h6>
              <FormInput
                defaultValue={control.healthGoal}
                disabled={!edit}
                style={!edit ? {background: "transparent", border: "none", fontSize: '1rem', color: "#5a6169"} : {fontSize: '1rem',  color: "#5a6169"}}
                name="healthGoal"
                onChange={handleChange}
              />
            </Col>
            <Col className={`d-flex ${edit ? 'flex-column' : 'flex-row'}`}>
              <h6 className="mr-2">Food Prefence:</h6>
              {edit && (
                <div className="d-flex flex-row">
                  <FormInput
                    name="foodPreference"
                    placeholder="Add food item"
                    style={{ width: 200 }}
                    className="ml-3 mr-3"
                    onChange={({ target }) =>
                      setCurrentFoodPreference(target.value)
                    }
                  />
                  <Button onClick={addFoodPreference}>
                    <span>+</span>Add
                  </Button>
                </div>
              )}
              <span className={`${edit && 'border-bottom'}`}>
                {foodPreference.join(", ").toString()}
              </span>
            </Col>
          </Col>
          {assignedDietitian && (
            <Col className="d-flex flex-row mt-2 border-bottom">
              <h6 className="mr-2">Dietitian:</h6> {"  "}
              <span>{assignedDietitian.Dietitian.name}</span>
            </Col>
          )}
          <Col className="d-flex flex-row mt-2 align-items-center">
            <h6 className="mr-2 mb-0">Subscription:</h6>
            <CustomSelect
              label=""
              options={[
                "Try Out Package",
                "Basic Package",
                "Platinum Package",
                "Premium Package",
              ]}
              type="edit"
              disabled={!edit}
              handleChange={handleChange}
              selected={control.subscriptionPackage}
            />
          </Col>
          <Col className="mb-3">
            {(control.subscriptionStart || control.subscriptionEnd) && (
              <>
                <Col className="d-flex flex-row">
                  <h6 className="mr-2">Subscription Start:</h6>
                  <FormInput
                    type="date"
                    name="subscriptionStartDate"
                    onChange={handleChange}
                    defaultValue={control.subscriptionStartDate}
                    disabled={!edit}
                  />
                </Col>
                <Col className="d-flex flex-row">
                  <h6 className="mr-2">Subscription End:</h6>
                  <FormInput
                    type="date"
                    name="subscriptionEndDate"
                    onChange={handleChange}
                    defaultValue={control.subscriptionEndDate}
                    disabled={!edit}
                  />
                </Col>
              </>
            )}
          </Col>
          <div className="d-flex flew-row">
            <Button
              className="ml-3"
              onClick={edit ? submitChanges : () => setEdit(true)}
            >
              {edit ? "Submit Changes" : "Edit Patient Profile"}
            </Button>
            {edit && (
              <Button
                className="ml-3"
                onClick={() => {
                  // restore();
                  setEdit(false);
                }}
              >
                Cancel Changes
              </Button>
            )}
          </div>
        </Card>
      </Col>
    </Container>
  );
};

export default PatientDetails;
