import React from "react";
import { Container, Row, Col, Card, CardHeader, Button } from "shards-react";

import PageTitle from "../../components/common/PageTitle";
import patient from "../../components/user-profile-lite/patient";
import UserAccountDetails from "../../components/user-profile-lite/UserAccountDetails";

const PatientDetails = () => {
  const patient = JSON.parse(localStorage.getItem("patient"));

  console.log(patient);
  return (
    <Container fluid className="main-content-container px-4">
      <Row noGutters className="page-header py-4">
        <PageTitle
          title="Patient Profile"
          subtitle="Overview"
          md="12"
          className="ml-sm-auto mr-sm-auto"
        />
      </Row>
      <Col
        lg="12"
        className="d-flex flex-column justify-content-center align-items-center"
      >
        <Card style={{ width: "100%", maxWidth: "700px", height: "500px" }}>
          <CardHeader className="border-bottom text-center">
            <div className="mb-3 mx-auto">
              <img
                className="rounded-circle"
                src={patient.avatar}
                alt={patient.name}
                width="110"
              />
            </div>
            <h4 className="mb-0">{patient.name}</h4>
            <span className="text-muted d-block mb-2">
              {patient.jobTitle}
            </span>
            <Button pill outline size="sm" className="mb-2">
              <i className="material-icons mr-1">person_add</i> Follow
            </Button>
          </CardHeader>
        </Card>
      </Col>
    </Container>
  );
};

export default PatientDetails;
