import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import {
  Container,
  Row,
  Card,
  CardHeader,
  CardBody,
  Col,
  Button,
  Alert,
} from "shards-react";
import { FormInput } from "shards-react";
import PageSpinner from "../../components/common/PageSpinner";
import PageTitle from "../../components/common/PageTitle";
import { getAdmins, updateCompanyDataEndpoint } from "../../apiConstants/apiConstants";
import { updateCompanyData } from "../../actions/superadmin/authAction";

export default function SuperAdminProfile() {
  const dispatch = useDispatch();
  const profile = JSON.parse(localStorage.getItem("loggedInUser"));

  const [edit, setEdit] = useState(false);
  const [control, setControl] = useState(profile);
  const [updated, setUpdated] = useState(false);
  const updatedCompanyData = useSelector((state) => state.updateCompanyData);
  const [alert, setAlert] = useState(false);
  const [emailChanged, setEmailChanged] = useState(false);
  const handleChange = ({ target }) => {
    setControl({ ...control, [target.name]: target.value });
  };

  useEffect(() => {
    const endpoint = getAdmins + profile.companyCode;
    dispatch(getAllAdmins(getAdmins))
  }, []);

  const submitChanges = () => {
    let endpoint = updateCompanyDataEndpoint;
    delete control.createdAt;
    delete control.updatedAt;
    delete control.id;
    console.log(control);
    if (control.email !== profile.email) {
      setEmailChanged(true);
      // dispatch(updateCompanyData(endpoint, control));
    } else {
      // dispatch(updateCompanyData(endpoint, control));
      setUpdated(true);
      setEmailChanged(false);
    }
  };

  useEffect(() => {
    if (updated && updateCompanyData.isSuccessful && updateCompanyData.data) {
      if (emailChanged) {
        alert("Changed email. Kindly login with new email");
        setAlert(false);
        window.localStorage.clear();
        window.location.pathname = "/interface";
      } else {
        setAlert(true);
      }
    }
  }, [updatedCompanyData, updated]);

  useEffect(() => {
    if (alert) {
      setTimeout(() => {
        setAlert(false);
        window.location.reload();
      }, 2000);
    }
  }, [alert]);

  const restore = () => {
    setControl(profile);
  };

  if (!control) {
    return <PageSpinner />;
  }

  return (
    <Container style={{ minHeight: "100vh" }}>
      {alert && (
        <Container fluid className="px-0 sticky-top">
          <Alert className="mb-0 text-center">
            <i className="fa fa-info mx-2"></i>Editted Company Profile.
          </Alert>
        </Container>
      )}
      <Row noGutters className="page-header py-4">
        <PageTitle
          sm="4"
          title="Profile"
          subtitle="Dashboard"
          className="text-sm-left"
        />
      </Row>

      <Card style={{ width: "100%", maxWidth: "506px" }} className="mx-auto">
        <CardHeader
          className="border-bottom"
          style={{ fontSize: "1.2rem", fontWeight: "bold" }}
        >
          Company Profile
        </CardHeader>
        <CardBody>
          <Col className="d-flex flex-row align-items-center">
            <h6 className="mb-0 mr-2">Name:</h6>
            <FormInput
              defaultValue={`${control.name}`}
              disabled={!edit}
              name="name"
              style={
                !edit
                  ? {
                      background: "transparent",
                      border: "none",
                      fontSize: "1rem",
                    }
                  : { fontSize: "1rem" }
              }
              onChange={handleChange}
            />
          </Col>
          <Col className="d-flex flex-row align-items-center">
            <h6 className="mb-0 mr-2">Email:</h6>
            <FormInput
              defaultValue={`${control.email}`}
              disabled={!edit}
              name="name"
              type="email"
              style={
                !edit
                  ? {
                      background: "transparent",
                      border: "none",
                      fontSize: "1rem",
                    }
                  : { fontSize: "1rem" }
              }
              onChange={handleChange}
            />
          </Col>
          <Col className="d-flex flex-row align-items-center">
            <h6 className="mb-0 mr-2">Phone:</h6>
            <FormInput
              defaultValue={`${control.phone}`}
              disabled={!edit}
              name="name"
              type="email"
              style={
                !edit
                  ? {
                      background: "transparent",
                      border: "none",
                      fontSize: "1rem",
                    }
                  : { fontSize: "1rem" }
              }
              onChange={handleChange}
            />
          </Col>
          <Col className="d-flex flex-row align-items-center">
            <h6 className="mb-0 mr-2">Role:</h6>
            <span>Admin</span>
          </Col>

          <div className="d-flex flew-row mt-5">
            <Button
              size="sm"
              theme="primary"
              className="ml-3"
              onClick={edit ? submitChanges : () => setEdit(true)}
            >
              {edit ? "Submit Changes" : "Update Profile"}
            </Button>
            {edit && (
              <Button
                size="sm"
                theme="danger"
                className="ml-3"
                onClick={() => {
                  restore();
                  setEdit(false);
                }}
              >
                Cancel Changes
              </Button>
            )}
          </div>
        </CardBody>
      </Card>
    </Container>
  );
}
